import React , {useState} from 'react';


const Blog = () => {
  const [formData, setFormData] = useState([]);
  const [input, setInput] = useState(''); 
  const handleChange = (e) => {
    setInput(e.target.value);
};

const handleSubmit = (e) => {
  e.preventDefault();
  setFormData([...formData, input]);
  setInput(''); 
};

return (
    <div>
      <h1>Novo Post</h1>
      <form onSubmit={handleSubmit}>
        <label>
        <textarea value={input} onChange={handleChange} rows="5" cols="50" />
        </label>
        <button className="botao" type="submit">Postar</button>
      </form>

      <div>
        <h2>Posts:</h2>
        {formData.map((post, index) => (
          <div key={index} style={{ marginBottom: '20px', whiteSpace: 'pre-wrap' }}>
            {post}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
