import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Blog = () => {
  const [formData, setFormData] = useState([]);
  const [input, setInput] = useState('');

  // Buscar dados armazenados ao montar o componente
  useEffect(() => {
    axios.get('http://localhost:3001/api/posts')
      .then(response => {
        setFormData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  // Função para lidar com a mudança no campo do formulário
  const handleChange = (e) => {
    setInput(e.target.value);
  };

  // Função para lidar com a submissão do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim()) {
      axios.post('http://localhost:3001/api/posts', { text: input })
        .then(response => {
          setFormData([...formData, response.data]);
          setInput(''); // Limpar o campo de input após a submissão
        })
        .catch(error => {
          console.error('Error posting data:', error);
        });
    }
  };


