//   setInput(''); 
// };

return (
    <div>
      <h1>Novo Post</h1>
      <form onSubmit={handleSubmit}>
        <label>
        <textarea value={input} onChange={handleChange} rows="5" cols="50" />
        </label>
        <button className="botao" type="submit">Postar</button>
      </form>

      <div>
        <h2>Posts:</h2>
        {formData.map((post, index) => (
          <div key={index} style={{ marginBottom: '20px', whiteSpace: 'pre-wrap' }}>
            {post.text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Blog;
